/**
 * EXPORTS
 */
export interface IBaseResponse<T> {
    body: T;
    failure: boolean;
    status: number;
}

export interface IFailure<T> extends IBaseResponse<T> {
    failure: true;
}

export interface ISuccess<T> extends IBaseResponse<T> {
    failure: false;
}

export type IResponse<A, B> = IFailure<A> | ISuccess<B>;
