/**
 * TYPES
 */
import {IBaseResponse} from 'self/datasources/requester.d';
import {IFailure} from 'self/datasources/requester.d';


/**
 * CODE
 */

/**
 * I return if a request response is successful or not.
 *
 * :param response: request response
 *
 * :returns: if the response is successful or not
 */
// eslint-disable-next-line @typescript-eslint/no-explicit-any
function isFailure (response: IBaseResponse<any>): response is IFailure<any>
{
    return response.failure === true;
}

/**
 * EXPORTS
 */
export {
    isFailure,
};
