/**
 * TYPES
 */
import {IPost as IData} from 'self/datasources/redditmediator.d';
import {IPost as ISchema} from 'self/schema.d';


/**
 * CODE
 */
function reduce (post: IData): ISchema
{
    return {
        content: post.Content,
        id: post.Id,
        name: post.Name,
        subReddit: post.SubReddit,
        title: post.Title,
        type: post.Type,
    };
}


/**
 * EXPORTS
 */
export {
    reduce,
};
