/**
 * I log a received request.
 *
 * :param params: request parameters
 *
 * :returns: nothing
 */
// eslint-disable-next-line @typescript-eslint/no-explicit-any
function logRequest (params: any): void
{
    // get request data
    const {operationName, query} = params.req.body;

    // log request (query and mutations)
    if (operationName !== 'IntrospectionQuery')
    {
        console.log( // eslint-disable-line no-console
            `Received request:\n${query}`
        );
    }
}


/**
 * EXPORTS
 */
export {
    logRequest
};
